clear all;clf;clc;%soal a%Bangkitkan bilangan random dengan fungsi randn()A = randn(2,2);b = randn(2,1);p = randn(2,1);s = randn(2,1);%soal b%Definisi awal variabel untuk solusisyms y1(t) y2(t)%Definisi ulang ODE% Ay(t) = [A(1,1)*y1(t)+A(1,2)*y2(t)]ODE = [diff(y1) == [A(1,1)*y1(t)+A(1,2)*y2(t)]+(b(1)*sin(p(1)*t)),diff(y2) == [A(2,1)*y1(t)+A(2,2)*y2(t)]+(b(2)*sin(p(2)*t))];%Definisi ulang ICic = [y1(0)==s(1),y2(0)==s(2)];%Komputasi solusi analitik[y_analitik1(t),y_analitik2(t)] = dsolve(ODE, ic)%soal c%Rentang sumbu horizontal dari 0 sampai 10tspan = [0 10];%Definisi ulang kondisi awaly0 = [s(1);s(2)];%Komputasi solusi numerik[t,y_numerik] = ode45(@(t,y_numerik) pers(t,y_numerik,A,b,p), tspan, y0);%soal d%Definisi error tiap segmen horizontalerror = zeros(size(t,1),2);%Perhitungan error tiap segmenfor i=1:1:size(t,1)%norm untuk bilangan kompleks, abs untuk bilangan real  error(i,1) = norm(y_numerik(i,1) - y_analitik1(t(i)),2);  error(i,2) = norm(y_numerik(i,2) - y_analitik2(t(i)),2);end%Plot solusi analitik dan numerik dalam 1 grafikfigure(1)fplot(y_analitik1,'-b')hold onfplot(y_analitik2,'-r')hold onplot(t,y_numerik(:,1),'b',t,y_numerik(:,2),'r')hold off%ylim([-10 10]);xlim([0 10]);legend('yAnalitis1','yAnalitis2','yNumerik1','yNumerik2')title('Solusi Analitis dan Numerik');xlabel('t')ylabel('y')%Plot error yang dihasilkanfigure(2)plot(t,error(:,1),'b',t,error(:,2),'r')legend('y1','y2')title('Galat Absolut');xlabel('t')ylabel('-')%Sub-fungsi untuk perhitungan numerikfunction dydt = pers(t,y_numerik,A,b,p)  dydt = [A(1,1)*y_numerik(1)+A(1,2)*y_numerik(2)+(b(1)*sin(p(1)*t)) ; A(2,1)*y_numerik(1)+A(2,2)*y_numerik(2)+(b(2)*sin(p(2)*t))];end